<script setup lang="ts">
import { computed, ref } from 'vue'
import Card from '../components/Card.vue'
import BaseInput from '../components/base/BaseInput.vue'
// Check out https://vuejs.org/api/sfc-script-setup.html#script-setup

interface Filter {
	name: string
	value: string
}

interface Position {
	lat: number
	lng: number
}

interface Item {
	name: string
	description: string
	price: string
	priceUnit: string
	postalcode: string
	address: string
	position: Position
	images: string[]
	startDate: string
	endDate: string
	categories: string[]
	filters: Filter[]
}

enum Status {
	ACTIVE = 'Active',
	ARCHIVED = 'Archived',
}

interface LoanedItem {
	id: number
	name: string
	image: string
	loanStartDate: string
	loanEndDate: string
	price: string
	priceUnit: string
	status: 'Active' | 'Archived'
}

const items = ref<LoanedItem[]>([
	{
		id: 123,
		name: 'Epic stol',
		loanStartDate: '2022-04-20T18:00:00',
		loanEndDate: '2022-06-20T18:00:00',
		image: 'data:image/jpeg;base64,',
		price: '100',
		priceUnit: 'dag',
		status: 'Active',
	},
	{
		id: 123,
		name: 'Epic stol',
		loanStartDate: '2022-04-20T18:00:00',
		loanEndDate: '2022-06-20T18:00:00',
		image: 'data:image/jpeg;base64,',
		price: '100',
		priceUnit: 'dag',
		status: 'Active',
	},
	{
		id: 123,
		name: 'Epic stol',
		loanStartDate: '2022-04-20T18:00:00',
		loanEndDate: '2022-06-20T18:00:00',
		image: 'data:image/jpeg;base64,',
		price: '100',
		priceUnit: 'dag',
		status: 'Active',
	},
	{
		id: 123,
		name: 'Amazing stol',
		loanStartDate: '2022-04-20T18:00:00',
		loanEndDate: '2022-06-20T18:00:00',
		image: 'data:image/jpeg;base64,',
		price: '100',
		priceUnit: 'dag',
		status: 'Archived',
	},
])

const selectedTag = ref<Status>(Status.ACTIVE)
const search = ref('')

const displayedItems = computed(() => {
	switch (selectedTag.value) {
		case Status.ACTIVE:
			return items.value
				.filter(el => el.status === 'Active')
				.filter(el =>
					el.name.toLowerCase().includes(search.value.toLowerCase())
				)
		case Status.ARCHIVED:
			return items.value
				.filter(el => el.status === 'Archived')
				.filter(el =>
					el.name.toLowerCase().includes(search.value.toLowerCase())
				)
	}
})
</script>

<template>
	<div v-if="items?.length > 0" class="grid gap-4">
		<h1 class="text-4xl font-bold">Mine lån</h1>
		<div class="flex gap-4">
			<button
				class="px-2 py-1 rounded-lg"
				:class="
					selectedTag === tag
						? 'bg-blue text-white'
						: 'bg-slate-300 text-slate-900'
				"
				@click="selectedTag = tag"
				v-for="tag in Status"
			>
				{{ tag }}
			</button>
		</div>
		<BaseInput label="Søk" v-model="search"></BaseInput>
		<div class="grid gap-4">
			<router-link
				class="bg-slate-100 rounded-lg shadow-lg"
				v-for="item in displayedItems"
				:to="`/item/${item.id}`"
			>
				<div class="flex gap-2">
					<img
						class="w-32 rounded-l-lg"
						:src="item.image"
						:alt="item.name"
					/>
					<div class="p-2 grid">
						<p class="font-bold text-lg">
							{{ item.name }}
						</p>
						<div>
							<p>{{ item.price }}kr / {{ item.priceUnit }}</p>
						</div>
						<p class="text-slate-500">
							{{
								new Date(
									item.loanStartDate
								).toLocaleDateString()
							}}
							-
							{{
								new Date(item.loanEndDate).toLocaleDateString()
							}}
						</p>
					</div>
				</div>
			</router-link>
		</div>
	</div>
	<p v-else>No items</p>
</template>
